con:config.script:
// log is used to print the information on the console. Can be used with "", '' and works with or w/o semicolon
log.info 'hello World'
/* In Groovy script context is used to access the properties associated with the test case. Please note that scope is limited to individual test case and to Test Suite. You will not be able to access the properties from other test cases */ log.info context.expand('${#TestCase#name}')
log.info context.expand('${#TestSuite#id}')
/* * TestRunner is used to modify/access any property associated with another test case, test step etc..Use getPropertyValue and setPropertyValue * to set/get the property details */ log.info testRunner.testCase.testSuite.testCases["GetEmployee"].getPropertyValue("Name")
log.info testRunner.testCase.testSuite.testCases["Add Employee"].getPropertyValue("Age")
log.info testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["SOAP Request"].getPropertyValue("Request");
testRunner.testCase.testSuite.testCases["Add Employee"].setPropertyValue("Age","20")
log.info testRunner.testCase.testSuite.testCases["Add Employee"].getPropertyValue("Age")
log.info testRunner.testCase.testSuite.project.getPropertyValue("ProjectLevel")
def project = testRunner.testCase.testSuite.project
log.info project.getPropertyValue("ProjectLevel")

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

def addEmpReq = testRunner.testCase.testSuite.testCases["Add Employee"].testSteps["SOAP Request"].getPropertyValue("Request")

def xmlAdd = new XmlHolder(addEmpReq);

xmlAdd.setNodeValue("//typ:addEmployee/typ:name","KumarTestB")
xmlAdd.setNodeValue("//typ:addEmployee/typ:id","061")
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department","IT")
xmlAdd.setNodeValue("//typ:addEmployee/typ:age","35")

def empName = xmlAdd.getNodeValue("//typ:addEmployee/typ:name")
log.info empName

def newXmlAdd = xmlAdd.getXml()

testRunner.testCase.testSuite.testCases["Add Employee"].testSteps["SOAP Request"].setPropertyValue("Request",newXmlAdd)

/*
 To run the test step we need to get hold of the individual test step
 We also need the context of the test case for which we are executing the test step. This is achieved by importing the 
 package WSDLTestRunContext
 Use the run method to execute the step
 */

def addEmplTestStep = testRunner.testCase.testSuite.testCases["Add Employee"].testSteps["SOAP Request"];
def context = new WsdlTestRunContext(addEmplTestStep);

addEmplTestStep.run(testRunner,context)

//Validate the employee have been successfully created by performing the request from getEmployee details

def getEmployeeReq = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["SOAP Request"].getPropertyValue("Request");
def getEmployeeXML = new XmlHolder(getEmployeeReq);

getEmployeeXML.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",empName);
testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["SOAP Request"].setPropertyValue("Request",getEmployeeXML.getXml());

def getEmplTestStep = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["SOAP Request"];
def getEmpContext = new WsdlTestRunContext(getEmplTestStep);

getEmplTestStep.run(testRunner,getEmpContext);

def getEmplRes = testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["SOAP Request"].getPropertyValue("Response");
def getEmpResXmlHolder = new XmlHolder(getEmplRes);

def empFromRes = getEmpResXmlHolder.getNodeValue("//ns:getEmployeeDetailsResponse/ns:return/ns:name");

if(empFromRes.equals(empName))
assert empFromRes == empName
